from Crypto.Util.number import getPrime, bytes_to_long, long_to_bytes
import gmpy2 as gp
import random
from flag import flag
flag = bytes_to_long(flag)
p = getPrime(100)
q = getPrime(100)
n = p * q
clist = []
for i in bin(flag)[2:]:
    while True:
        x = random.randint(1, n)#1-n的随机数
        if gp.gcd(x, n) == 1:#最大公约数为1
            c = (pow(3, int(i) + x * 2, n) * x**2) % n
            clist.append(c)
            break
print(n)

print(clist)
